// src/ItemList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ITEMS_PER_PAGE = 10;

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [filteredItems, setFilteredItems] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://localhost:5005/api/items');
        setItems(response.data);
        setFilteredItems(response.data.slice(0, ITEMS_PER_PAGE));
      } catch (error) {
        setError('Error fetching items');
      }
    };

    fetchItems();
  }, []);

  useEffect(() => {
    setFilteredItems(
      items.slice((currentPage - 1) * ITEMS_PER_PAGE, currentPage * ITEMS_PER_PAGE)
    );
  }, [currentPage, items]);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container">
      <h2>Item List</h2>
      <ul className="item-list">
        {filteredItems.map(item => (
          <li key={item._id} className="item">
            <h3>{item.name}</h3>
            <p>{item.description}</p>
          </li>
        ))}
      </ul>
      <div className="pagination">
        {[...Array(Math.ceil(items.length / ITEMS_PER_PAGE))].map((_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={currentPage === index + 1 ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ItemList;
